/*"1-" para cliente, "2-" para profissional e "3-" para empresa*/

/*Avaliação do Prestador
SELECT * FROM AVALIACAOS WHERE COD_AVALIADO LIKE '2%';

SELECT COD_AVALIADO, AVG(NOTA) AS NOTA_MEDIA_PRESTADOR FROM AVALIACAOS WHERE COD_AVALIADO LIKE '2%' GROUP BY COD_AVALIADO;
*/
SELECT A.COD_AVALIADO, B.COD_USUARIO, AVG(A.NOTA)  AS NOTA_MEDIA_PRESTADOR, B.NOME FROM AVALIACAOS AS A JOIN USUARIO AS B ON A.COD_AVALIADO =  B.COD_USUARIO WHERE COD_AVALIADO LIKE '2%' GROUP BY A.COD_AVALIADO ORDER BY AVG(A.NOTA) DESC LIMIT 3;


/*Avaliação do Cliente*/
SELECT AVG(NOTA) AS NOTA_MEDIA_CLIENTE FROM AVALIACAOS WHERE COD_AVALIADO LIKE '1%';


/*Tempo para a realização do serviço*/
SELECT SUM(DATEDIFF (DATA_PAGAMENTO,DATA_SERVICO)) / COUNT(DATA_SERVICO) AS TEMPO_SERVICO_MEDIO FROM AGENDAMENTOS GROUP BY MONTH(DATA_PAGAMENTO);

/*Serviços agendados*/
SELECT COUNT(DATA_SOLICITACAO) / COUNT(COD_SERVICO)* 100 AS SERVIÇOS_AGENDADOS FROM AGENDAMENTOS GROUP BY MONTH(DATA_SOLICITACAO) AND COD_SERVICO;

/*Cancelamento*/
SELECT COUNT(CASE WHEN STATUS LIKE 'Cancelado' THEN 1 END) / COUNT(CASE WHEN STATUS LIKE 'Concluído' THEN 1 END) *100  AS CANCELAMENTO FROM AGENDAMENTOS GROUP BY MONTH(DATA_SOLICITACAO);

/*Cadastro de Usuários*/
SELECT MONTH(DATA_CRIACAO), YEAR(DATA_CRIACAO), COUNT(DATA_CRIACAO) AS CADASTRO_USUARIOS FROM USUARIO GROUP BY MONTH(DATA_CRIACAO) , YEAR(DATA_CRIACAO) ORDER BY 1,2;

/*Cadastro de Prestadores*/
SELECT MONTH(DATA_CRIACAO), YEAR(DATA_CRIACAO), COUNT(CASE WHEN COD_TIPO IS NOT NULL THEN 1 END)  AS CADASTRO_PRESTADORES FROM USUARIO GROUP BY MONTH(DATA_CRIACAO) , YEAR(DATA_CRIACAO) ORDER BY 1,2;

/*Serviços mais contratados*/
SELECT B.NOME, COUNT(CASE WHEN A.COD_TIPO IS NOT NULL THEN 1 END) AS SERVICOS_MAIS_CONTRATADOS FROM AGENDAMENTOS AS A JOIN TIPO_SERVICO AS B ON A.COD_TIPO = B.COD_TIPO GROUP BY A.COD_TIPO ORDER BY COUNT(CASE WHEN COD_TIPO IS NOT NULL THEN 1 END) DESC LIMIT 3;

/*Preço médio dos serviços*/
SELECT B.NOME, SUM(A.VALOR_ORCAMENTO) / COUNT(CASE WHEN A.COD_TIPO IS NOT NULL THEN 1 END) AS PRECO_MEDIO_SERVICO FROM AGENDAMENTOS AS A JOIN SERVICOS AS B ON A.COD_TIPO = B.COD_TIPO WHERE A.VALOR_ORCAMENTO IS NOT NULL GROUP BY A.COD_TIPO;

/*Quantidade de serviços pendentes*/
SELECT COUNT(CASE WHEN STATUS LIKE 'Pendente' THEN 1 END) / COUNT(CASE WHEN COD_TIPO IS NOT NULL THEN 1 END) *100 AS CANCELAMENTO FROM AGENDAMENTOS GROUP BY MONTH(DATA_SOLICITACAO);

/*Quantidade de serviços não pagos*/
SELECT COUNT(CASE WHEN STATUS LIKE 'Nâo Pago' THEN 1 END) / COUNT(CASE WHEN COD_TIPO IS NOT NULL THEN 1 END) *100 AS CANCELAMENTO FROM AGENDAMENTOS GROUP BY MONTH(DATA_SOLICITACAO);

